<?php

declare(strict_types=1);

function traverse_hotel(string $instructions, bool $stopOnBasementFirstVisit = false): int
{
    $currentFloor = 0;
    foreach (str_split($instructions) as $pos => $instruction) {
        if ($instruction === '(') {
            $currentFloor += 1;
        } elseif ($instruction === ')') {
            $currentFloor -= 1;
        }
        if ($stopOnBasementFirstVisit && $currentFloor === -1) {
            return $pos + 1;
        }
    }

    return $currentFloor;
}


